#!/usr/bin/env pwsh`n# Provision an Azure SQL Database`n`nparam (`n[string]$ResourceGroupName,`n[string]$ServerName,`n[string]$DatabaseName,`n[string]$Location,`n[string]$AdminUser,`n[string]$AdminPassword`n)`n`nNew-AzSqlServer -ResourceGroupName $ResourceGroupName -Location $Location -ServerName $ServerName -SqlAdministratorCredentials $(New-Object -TypeName PSCredential -ArgumentList $AdminUser, (ConvertTo-SecureString -String $AdminPassword -AsPlainText -Force))